import React from 'react';
import './AlbumHome.css';
import BlogPost from './BlogPost';
import WideBlock from './WideBlock';
import GalleryRow from './GalleryRow';

const imageFiles = [
  'Imagen1.jpg','Imagen2.jpg','Imagen3.jpg','Imagen4.jpg','Imagen5.jpg','Imagen6.jpg','Imagen7.jpg','Imagen8.jpg','Imagen9.jpg','Imagen10.jpg','Imagen11.jpg','Imagen12.jpg','Imagen13.jpg','Imagen14.jpg','Imagen15.jpg','Imagen16.jpg','Imagen17.jpg','Imagen18.jpg','Imagen19.jpg','Imagen20.jpg','Imagen21.jpg','Imagen22.jpg','Imagen23.jpg','Imagen24.jpg','Imagen25.jpg','Imagen26.jpg','Imagen27.jpg','Imagen28.jpg','Imagen29.jpg','Imagen30.jpg','Imagen31.jpg','Imagen32.jpg','Imagen33.jpg','Imagen34.jpg','Imagen35.jpg','Imagen36.jpg']

const videoFiles = [
  'video1.mp4','video2.mp4','video3.mp4','video4.mp4','video5.mp4','video6.mp4','video7.mp4','video8.mp4'
];


function AlbumHome() {
  // Fechas simuladas para el ejemplo de blog
  const fechas = [
    '20 de abril de 2025',
    '19 de abril de 2025',
    '18 de abril de 2025',
    '17 de abril de 2025',
    '16 de abril de 2025',
    '15 de abril de 2025',
    '14 de abril de 2025',
  ];

  return (
    <main className="album-home">
      <header className="album-header">
        <h1>PortalAlbum</h1>
        <h2>2º Aniversario de Margarita & Elí</h2>
        <p className="album-desc">Un espacio para revivir juntos los mejores recuerdos en fotos, videos y canciones.</p>
      </header>
      <section className="blog-section">
        {/* Fila principal: 4 posts clásicos */}
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[0]}
          title="Recuerdo #1"
          meta={fechas[0]}
          desc="Una foto para el recuerdo."
        />
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[1]}
          title="Recuerdo #2"
          meta={fechas[1]}
          desc="Otro momento especial."
        />
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[2]}
          title="Recuerdo #3"
          meta={fechas[2]}
          desc="Un instante inolvidable."
        />
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[3]}
          title="Recuerdo #4"
          meta={fechas[3]}
          desc="Un día para recordar."
        />
        {/* Bloque ancho: About, ocupa 2 columnas */}
        <div style={{gridColumn: '1 / span 2'}}>
          <WideBlock
            image={process.env.PUBLIC_URL + '/media/' + imageFiles[4]}
            title="Sobre Margarita & Elí"
            desc="Nuestra historia juntos está llena de momentos hermosos, risas y sueños compartidos. Este álbum es un pequeño homenaje a todo lo vivido y lo que vendrá."
          />
        </div>
        {/* Imagen destacada: ocupa 2 columnas */}
        <div className="image-block" style={{gridColumn: '3 / span 2', minHeight: 260}}>
          <img src={process.env.PUBLIC_URL + '/media/' + imageFiles[5]} alt="Imagen destacada" />
        </div>
        {/* Galería horizontal de fotos pequeñas */}
        <div style={{gridColumn: '1 / -1'}}>
          <GalleryRow
            images={imageFiles.slice(6, 12).map((img, idx) => ({
              src: process.env.PUBLIC_URL + '/media/' + img,
              alt: `Miniatura ${idx + 7}`
            }))}
          />
        </div>
        {/* Más posts clásicos */}
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[12]}
          title="Recuerdo #5"
          meta={fechas[4]}
          desc="Un día muy especial."
        />
        <BlogPost
          video={process.env.PUBLIC_URL + '/media/' + videoFiles[0]}
          title="Video especial"
          meta={fechas[5]}
          desc="Un recuerdo en video para revivir juntos."
        />
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[13]}
          title="Recuerdo #6"
          meta={fechas[5]}
          desc="Otro instante para atesorar."
        />
        <BlogPost
          image={process.env.PUBLIC_URL + '/media/' + imageFiles[14]}
          title="Recuerdo #7"
          meta={fechas[6]}
          desc="Un recuerdo más para nuestra historia."
        />
        {/* Canción especial, ocupa todo el ancho */}
        <div className="blog-post" style={{gridColumn: '1 / -1'}}>
          <div className="post-image empty-media" style={{display:'flex',alignItems:'center',justifyContent:'center',background:'#f7f7f7'}}>
            <span role="img" aria-label="Canción" style={{fontSize:'2.4rem'}}>🎵</span>
          </div>
          <div className="post-body">
            <div className="post-title">Canción especial</div>
            <div className="post-meta">20 de abril de 2025</div>
            <div className="post-desc">Escucha la canción que acompaña este aniversario:</div>
            <div className="youtube-audio-wrapper" style={{marginTop:'8px'}}>
              <iframe
                width="100%"
                height="60"
                src="https://www.youtube.com/embed/BPidLpADlaM?autoplay=0&controls=1"
                title="Canción especial"
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                style={{borderRadius: '10px', background: '#000'}}
              ></iframe>
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}

export default AlbumHome;
