import React from 'react';
import './AlbumHome.css';
import BlogPost from './BlogPost';
import WideBlock from './WideBlock';
import GalleryRow from './GalleryRow';

const imageFiles = [
  'Imagen1.jpg','Imagen2.jpg','Imagen3.jpg','Imagen4.jpg','Imagen5.jpg','Imagen6.jpg','Imagen7.jpg','Imagen8.jpg','Imagen9.jpg','Imagen10.jpg','Imagen11.jpg','Imagen12.jpg','Imagen13.jpg','Imagen14.jpg','Imagen15.jpg','Imagen16.jpg','Imagen17.jpg','Imagen18.jpg','Imagen19.jpg','Imagen20.jpg','Imagen21.jpg','Imagen22.jpg','Imagen23.jpg','Imagen24.jpg','Imagen25.jpg','Imagen26.jpg','Imagen27.jpg','Imagen28.jpg','Imagen29.jpg','Imagen30.jpg','Imagen31.jpg','Imagen32.jpg','Imagen33.jpg','Imagen34.jpg','Imagen35.jpg','Imagen36.jpg']

const videoFiles = [
  'video1.mp4','video2.mp4','video3.mp4','video4.mp4','video5.mp4','video6.mp4','video7.mp4','video8.mp4'
];


function AlbumHome() {
  // Fechas simuladas para el ejemplo de blog
  const fechas = [
    '20 de abril de 2025',
    '19 de abril de 2025',
    '18 de abril de 2025',
    '17 de abril de 2025',
    '16 de abril de 2025',
    '15 de abril de 2025',
    '14 de abril de 2025',
  ];

  return (
    <main className="album-home">
      <header className="album-header">
        <h1>PortalAlbum</h1>
        <h2>2췈 Aniversario de Margarita & El칤</h2>
        <p className="album-desc">Un espacio para revivir juntos los mejores recuerdos en fotos, videos y canciones.</p>
      </header>
      <section className="blog-section">
        {/* Generar patr칩n repetitivo para todos los recuerdos */}
        {(() => {
          // Unificar im치genes y videos en una sola lista de recuerdos
          const recuerdos = [
            ...imageFiles.map((img, idx) => ({
              tipo: 'imagen',
              src: process.env.PUBLIC_URL + '/media/' + img,
              alt: `Foto ${idx + 1}`
            })),
            ...videoFiles.map((vid, idx) => ({
              tipo: 'video',
              src: process.env.PUBLIC_URL + '/media/' + vid,
              alt: `Video ${idx + 1}`
            }))
          ];
          // Ordenar para que im치genes y videos se alternen visualmente (opcional)
          // recuerdos.sort((a, b) => a.tipo === b.tipo ? 0 : a.tipo === 'imagen' ? -1 : 1);

          const bloques = [];
          let i = 0;
          let bloqueNum = 1;
          while (i < recuerdos.length) {
            // 1. 4 posts cl치sicos
            for (let j = 0; j < 4 && i < recuerdos.length; j++, i++, bloqueNum++) {
              const r = recuerdos[i];
              if (r.tipo === 'imagen') {
                bloques.push(
                  <BlogPost
                    key={`recuerdo${i}`}
                    image={r.src}
                    title={`Recuerdo #${bloqueNum}`}
                    meta={fechas[(i) % fechas.length]}
                    desc="Un recuerdo especial."
                  />
                );
              } else {
                bloques.push(
                  <BlogPost
                    key={`recuerdo${i}`}
                    video={r.src}
                    title={`Video #${bloqueNum}`}
                    meta={fechas[(i) % fechas.length]}
                    desc="Un recuerdo en video para revivir juntos."
                  />
                );
              }
            }
            // 2. WideBlock (si queda una imagen disponible)
            if (i < recuerdos.length && recuerdos[i].tipo === 'imagen') {
              bloques.push(
                <div style={{gridColumn: '1 / span 2'}} key={`wide${i}`}>
                  <WideBlock
                    image={recuerdos[i].src}
                    title="Sobre Margarita & El칤"
                    desc="Nuestra historia juntos est치 llena de momentos hermosos, risas y sue침os compartidos. Este 치lbum es un peque침o homenaje a todo lo vivido y lo que vendr치."
                  />
                </div>
              );
              i++;
            }
            // 3. Imagen destacada (si queda una imagen disponible)
            if (i < recuerdos.length && recuerdos[i].tipo === 'imagen') {
              bloques.push(
                <div className="image-block" style={{gridColumn: '3 / span 2', minHeight: 260}} key={`imgblock${i}`}>
                  <img src={recuerdos[i].src} alt="Imagen destacada" />
                </div>
              );
              i++;
            }
            // 4. Galer칤a horizontal (siguiente grupo de hasta 6 im치genes)
            const galeriaImgs = [];
            let galCount = 0;
            while (i < recuerdos.length && galCount < 6) {
              if (recuerdos[i].tipo === 'imagen') {
                galeriaImgs.push({src: recuerdos[i].src, alt: recuerdos[i].alt});
                galCount++;
              }
              i++;
            }
            if (galeriaImgs.length > 0) {
              bloques.push(
                <div style={{gridColumn: '1 / -1'}} key={`galeria${i}`}>
                  <GalleryRow images={galeriaImgs} />
                </div>
              );
            }
          }
          // Al final, bloque de canci칩n especial
          bloques.push(
            <div className="blog-post" style={{gridColumn: '1 / -1'}} key="cancion-especial">
              <div className="post-image empty-media" style={{display:'flex',alignItems:'center',justifyContent:'center',background:'#f7f7f7'}}>
                <span role="img" aria-label="Canci칩n" style={{fontSize:'2.4rem'}}>游꿧</span>
              </div>
              <div className="post-body">
                <div className="post-title">Canci칩n especial</div>
                <div className="post-meta">20 de abril de 2025</div>
                <div className="post-desc">Escucha la canci칩n que acompa침a este aniversario:</div>
                <div className="youtube-audio-wrapper" style={{marginTop:'8px'}}>
                  <iframe
                    width="100%"
                    height="60"
                    src="https://www.youtube.com/embed/BPidLpADlaM?autoplay=0&controls=1"
                    title="Canci칩n especial"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    style={{borderRadius: '10px', background: '#000'}}
                  ></iframe>
                </div>
              </div>
            </div>
          );
          return bloques;
        })()}
      </section>
    </main>
  );
}

export default AlbumHome;
