import React from 'react';
import './AlbumHome.css';
import BlogPost from './BlogPost';
import WideBlock from './WideBlock';
import GalleryRow from './GalleryRow';
import ModalMedia from './ModalMedia';

const imageFiles = [
  'Imagen1.jpg','Imagen2.jpg','Imagen3.jpg','Imagen4.jpg','Imagen5.jpg','Imagen6.jpg','Imagen7.jpg','Imagen8.jpg','Imagen9.jpg','Imagen10.jpg','Imagen11.jpg','Imagen12.jpg','Imagen13.jpg','Imagen14.jpg','Imagen15.jpg','Imagen16.jpg','Imagen17.jpg','Imagen18.jpg','Imagen19.jpg','Imagen20.jpg','Imagen21.jpg','Imagen22.jpg','Imagen23.jpg','Imagen24.jpg','Imagen25.jpg','Imagen26.jpg','Imagen27.jpg','Imagen28.jpg','Imagen29.jpg','Imagen30.jpg','Imagen31.jpg','Imagen32.jpg','Imagen33.jpg','Imagen34.jpg','Imagen35.jpg','Imagen36.jpg']

const videoFiles = [
  'video1.mp4','video2.mp4','video3.mp4','video4.mp4','video5.mp4','video6.mp4','video7.mp4','video8.mp4'
];


function AlbumHome() {
  // Fechas simuladas para el ejemplo de blog
  const fechas = [
    '20 de abril de 2025',
    '19 de abril de 2025',
    '18 de abril de 2025',
    '17 de abril de 2025',
    '16 de abril de 2025',
    '15 de abril de 2025',
    '14 de abril de 2025',
  ];

  const [modal, setModal] = React.useState({ open: false });

  const handleOpenModal = (data) => setModal({ open: true, ...data });
  const handleCloseModal = () => setModal({ open: false });

  return (
    <main className="album-home">
      <ModalMedia
        open={modal.open}
        onClose={handleCloseModal}
        tipo={modal.tipo}
        src={modal.src}
        alt={modal.alt}
        title={modal.title}
        meta={modal.meta}
        desc={modal.desc}
      />
      <header className="album-header">
        <h1>PortalAlbum</h1>
        <h2>2º Aniversario de Margarita & Elí</h2>
        <p className="album-desc">Un espacio para revivir juntos los mejores recuerdos en fotos, videos y canciones.</p>
      </header>
      <section className="blog-section">
        {/* Generar patrón repetitivo para todos los recuerdos */}
        {/* Todos los recuerdos como BlogPost iguales */}
        {(() => {
          const recuerdos = [
            ...imageFiles.map((img, idx) => ({
              tipo: 'imagen',
              src: process.env.PUBLIC_URL + '/media/' + img,
              alt: `Foto ${idx + 1}`
            })),
            ...videoFiles.map((vid, idx) => ({
              tipo: 'video',
              src: process.env.PUBLIC_URL + '/media/' + vid,
              alt: `Video ${idx + 1}`
            }))
          ];
          const bloques = [];
          let i = 0;
          let bloqueNum = 1;
          // 1. Primera fila: 4 bloques
          let fotosPrimeraFila = 0;
          let idxPrimeraFila = i;
          while (fotosPrimeraFila < 4 && idxPrimeraFila < recuerdos.length) {
            const r = recuerdos[idxPrimeraFila];
            if (r.tipo === 'imagen') {
              bloques.push(
                <div key={`recuerdo${idxPrimeraFila}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${bloqueNum + (idxPrimeraFila - i)}`, meta: fechas[idxPrimeraFila % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer' }}>
                  <BlogPost
                    image={r.src}
                    title={`Recuerdo #${bloqueNum + (idxPrimeraFila - i)}`}
                    meta={fechas[idxPrimeraFila % fechas.length]}
                    desc={'Un recuerdo especial.'}
                    imgClassName={'galeria-miniatura'}
                  />
                </div>
              );
              fotosPrimeraFila++;
            }
            idxPrimeraFila++;
          }
          bloqueNum += idxPrimeraFila - i;
          i = idxPrimeraFila;

          // 2. Segunda fila: dos bloques grandes (2 columnas cada uno)
          for (let j = 0; j < 2 && i < recuerdos.length; j++, i++, bloqueNum++) {
            const r = recuerdos[i];
            bloques.push(
              <div key={`recuerdo-large-${i}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`, meta: fechas[i % fechas.length], desc: r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', gridColumn: 'span 2' }}>
                <BlogPost
                  image={r.tipo === 'imagen' ? r.src : undefined}
                  video={r.tipo === 'video' ? r.src : undefined}
                  title={r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`}
                  meta={fechas[i % fechas.length]}
                  desc={r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.'}
                />
              </div>
            );
          }
          // 3. Tercera fila: galería horizontal de hasta 10 miniaturas SOLO de fotografías
          const galeria = [];
          let fotosAgregadas = 0;
          let galeriaIndex = i;
          while (galeriaIndex < recuerdos.length && fotosAgregadas < 10) {
            const r = recuerdos[galeriaIndex];
            if (r.tipo === 'imagen') {
              galeria.push(
                <div key={`recuerdo-mini-${galeriaIndex}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${bloqueNum + (galeriaIndex - i)}`, meta: fechas[galeriaIndex % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer', display: 'inline-block', width: '132px', margin: '0 0.5px' }}>
                  <img src={r.src} alt={`Recuerdo #${bloqueNum + (galeriaIndex - i)}`} className="post-image galeria-miniatura" style={{ width: '100%', borderRadius: '4px', objectFit: 'cover' }} />
                </div>
              );
              fotosAgregadas++;
            }
            galeriaIndex++;
          }
          // Avanza el índice principal la cantidad de recuerdos recorridos en la galería
          bloqueNum += galeriaIndex - i;
          i = galeriaIndex;

          if (galeria.length > 0) {
            bloques.push(
              <div key="galeria-row" style={{ gridColumn: '1 / -1', display: 'flex', justifyContent: 'center', gap: '8px', margin: '28px 0' }}>
                {galeria}
              </div>
            );
          }
          // 4. El resto de recuerdos en filas normales de 4 (como blog cards)
          while (i < recuerdos.length) {
            for (let j = 0; j < 4 && i < recuerdos.length; j++, i++, bloqueNum++) {
              const r = recuerdos[i];
              bloques.push(
                <div key={`recuerdo-resto-${i}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`, meta: fechas[i % fechas.length], desc: r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer' }}>
                  <BlogPost
                    image={r.tipo === 'imagen' ? r.src : undefined}
                    video={r.tipo === 'video' ? r.src : undefined}
                    title={r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`}
                    meta={fechas[i % fechas.length]}
                    desc={r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.'}
                  />
                </div>
              );
            }
          }
          // Canción especial al final
          bloques.push(
            <div className="blog-post" style={{gridColumn: '1 / -1'}} key="cancion-especial">
              <div className="post-image empty-media" style={{display:'flex',alignItems:'center',justifyContent:'center',background:'#f7f7f7'}}>
                <span role="img" aria-label="Canción" style={{fontSize:'2.4rem'}}>🎵</span>
              </div>
              <div className="post-body">
                <div className="post-title">Canción especial</div>
                <div className="post-meta">20 de abril de 2025</div>
                <div className="post-desc">Escucha la canción que acompaña este aniversario:</div>
                <div className="youtube-audio-wrapper" style={{marginTop:'8px'}}>
                  <iframe
                    width="100%"
                    height="60"
                    src="https://www.youtube.com/embed/BPidLpADlaM?autoplay=0&controls=1"
                    title="Canción especial"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    style={{borderRadius: '10px', background: '#000'}}
                  ></iframe>
                </div>
              </div>
            </div>
          );
          return bloques;
        })()}

      </section>
    </main>
  );
}

export default AlbumHome;
