import React from 'react';
import './AlbumHome.css';
import BlogPost from './BlogPost';
import WideBlock from './WideBlock';
import GalleryRow from './GalleryRow';
import ModalMedia from './ModalMedia';

const imageFiles = [
  'Imagen1.jpg','Imagen2.jpg','Imagen3.jpg','Imagen4.jpg','Imagen5.jpg','Imagen6.jpg','Imagen7.jpg','Imagen8.jpg','Imagen9.jpg','Imagen10.jpg','Imagen11.jpg','Imagen12.jpg','Imagen13.jpg','Imagen14.jpg','Imagen15.jpg','Imagen16.jpg','Imagen17.jpg','Imagen18.jpg','Imagen19.jpg','Imagen20.jpg','Imagen21.jpg','Imagen22.jpg','Imagen23.jpg','Imagen24.jpg','Imagen25.jpg','Imagen26.jpg','Imagen27.jpg','Imagen28.jpg','Imagen29.jpg','Imagen30.jpg','Imagen31.jpg','Imagen32.jpg','Imagen33.jpg','Imagen34.jpg','Imagen35.jpg','Imagen36.jpg']

const videoFiles = [
  'video1.mp4','video2.mp4','video3.mp4','video4.mp4','video5.mp4','video6.mp4','video7.mp4','video8.mp4'
];


function AlbumHome() {
  // Fechas simuladas para el ejemplo de blog
  const fechas = [
    '20 de abril de 2025',
    '19 de abril de 2025',
    '18 de abril de 2025',
    '17 de abril de 2025',
    '16 de abril de 2025',
    '15 de abril de 2025',
    '14 de abril de 2025',
  ];

  const [modal, setModal] = React.useState({ open: false });

  const handleOpenModal = (data) => setModal({ open: true, ...data });
  const handleCloseModal = () => setModal({ open: false });

  return (
    <main className="album-home">
      <ModalMedia
        open={modal.open}
        onClose={handleCloseModal}
        tipo={modal.tipo}
        src={modal.src}
        alt={modal.alt}
        title={modal.title}
        meta={modal.meta}
        desc={modal.desc}
      />
      <header className="album-header">
        <h1>PortalAlbum</h1>
        <h2>2º Aniversario de Margarita & Elí</h2>
        <p className="album-desc">Un espacio para revivir juntos los mejores recuerdos en fotos, videos y canciones.</p>
      </header>
      <section className="blog-section">
        {/* Generar patrón repetitivo para todos los recuerdos */}
        {/* Todos los recuerdos como BlogPost iguales */}
        {(() => {
          // --- SECCIÓN SOLO IMÁGENES ---
          const recuerdos = [
            ...imageFiles.map((img, idx) => ({
              tipo: 'imagen',
              src: process.env.PUBLIC_URL + '/media/' + img,
              alt: `Foto ${idx + 1}`
            })),
            ...videoFiles.map((vid, idx) => ({
              tipo: 'video',
              src: process.env.PUBLIC_URL + '/media/' + vid,
              alt: `Video ${idx + 1}`
            }))
          ];
          const bloques = [];
          let i = 0;
          let bloqueNum = 1;
          // Repetir patrón de 4 filas de forma cíclica SOLO para imágenes
          const soloImagenes = recuerdos.filter(r => r.tipo === 'imagen');
          let imgIdx = 0;
          let imgBloqueNum = 1;
          while (imgIdx < soloImagenes.length) {
            // 1. Primera fila: 4 bloques
            for (let j = 0; j < 4 && imgIdx < soloImagenes.length; j++, imgIdx++, imgBloqueNum++) {
              const r = soloImagenes[imgIdx];
              bloques.push(
                <div key={`recuerdo-img-${imgIdx}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${imgBloqueNum}`, meta: fechas[imgIdx % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer', marginBottom: j === 3 ? '0px' : undefined }}>
                  <img src={r.src} alt={`Recuerdo #${imgBloqueNum}`} className="post-image galeria-miniatura" style={{ width: '100%', borderRadius: '0', objectFit: 'cover' }} />
                </div>
              );
            }
            // 2. Segunda fila: bloque grande
            if (imgIdx < soloImagenes.length) {
              const r = soloImagenes[imgIdx];
              bloques.push(
                <div key={`recuerdo-large-img-${imgIdx}`} className="bloque-animado" onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${imgBloqueNum}`, meta: fechas[imgIdx % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer', gridColumn: '1 / -1', display: 'flex', alignItems: 'stretch', background: '#fff', borderRadius: '20px', minHeight: 220, padding: 0, width: '100%', maxHeight: 600 }}>
                  <>
                    <div style={{flex: '0 0 50%', minWidth: 0, height: '100%'}}>
                      <img src={r.src} alt={`Recuerdo #${imgBloqueNum}`} style={{ width: '100%', height: '100%', maxHeight: 600, objectFit: 'cover', borderRadius: '0 0 1.5px 1.5px', display: 'block' }} />
                    </div>
                    <div className="bloque-animado-text" style={{ padding: '0 32px', flex: '1 1 50%', display: 'flex', flexDirection: 'column', justifyContent: 'center', background: 'rgba(255,255,255,0.82)', borderRadius: '0 0 20px 20px' }}>
                      <div style={{ fontWeight: 700, fontSize: 22, color: '#444', marginBottom: 8 }}>{`Recuerdo #${imgBloqueNum}`}</div>
                      <div style={{ color: '#7a7a7a', fontSize: 16, marginBottom: 8 }}>{fechas[imgIdx % fechas.length]}</div>
                      <div style={{ color: '#8e7cc3', fontSize: 17 }}>Un recuerdo especial.</div>
                    </div>
                  </>
                </div>
              );
              imgIdx++;
              imgBloqueNum++;
            }
            // 3. Tercera fila: galería SOLO de imágenes
            const galeria = [];
            let fotosAgregadas = 0;
            let galeriaIndex = imgIdx;
            while (galeriaIndex < soloImagenes.length && fotosAgregadas < 10) {
              const r = soloImagenes[galeriaIndex];
              galeria.push(
                <div key={`recuerdo-mini-img-${galeriaIndex}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${imgBloqueNum + fotosAgregadas}`, meta: fechas[galeriaIndex % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer', display: 'inline-block', width: '132px', margin: '0 0.5px' }}>
                  <img src={r.src} alt={`Recuerdo #${imgBloqueNum + fotosAgregadas}`} className="post-image galeria-miniatura" style={{ width: '100%', borderRadius: '4px', objectFit: 'cover' }} />
                </div>
              );
              fotosAgregadas++;
              galeriaIndex++;
            }
            if (galeria.length > 0) {
              bloques.push(
                <div key={`galeria-row-img-${imgIdx}`} style={{ gridColumn: '1 / -1', display: 'flex', justifyContent: 'center', gap: '8px', margin: '0' }}>
                  {galeria}
                </div>
              );
            }
            imgIdx = galeriaIndex;
            imgBloqueNum += fotosAgregadas;
            // 4. Cuarta fila: bloque grande invertido
            if (imgIdx < soloImagenes.length) {
              const r = soloImagenes[imgIdx];
              bloques.push(
                <div key={`recuerdo-large4-img-${imgIdx}`} className="bloque-animado" onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${imgBloqueNum}`, meta: fechas[imgIdx % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer', gridColumn: '1 / -1', display: 'flex', alignItems: 'stretch', background: '#fff', borderRadius: '0 0 20px 20px', minHeight: 220, padding: 0, width: '100%', position: 'relative', top: '-2px', boxShadow: 'none' }}>
                  <>
                    <div className="bloque-animado-text" style={{ padding: '0 32px', flex: '1 1 50%', display: 'flex', flexDirection: 'column', justifyContent: 'center', background: 'rgba(255,255,255,0.82)', borderRadius: '0 0 20px 20px' }}>
                      <div style={{ fontWeight: 700, fontSize: 22, color: '#444', marginBottom: 8 }}>{`Recuerdo #${imgBloqueNum}`}</div>
                      <div style={{ color: '#7a7a7a', fontSize: 16, marginBottom: 8 }}>{fechas[imgIdx % fechas.length]}</div>
                      <div style={{ color: '#8e7cc3', fontSize: 17 }}>Un recuerdo especial.</div>
                    </div>
                    <div style={{flex: '0 0 50%', minWidth: 0, height: '100%'}}>
                      <img src={r.src} alt={`Recuerdo #${imgBloqueNum}`} className="galeria-miniatura" style={{ width: '100%', height: '100%', maxHeight: 600, objectFit: 'cover', borderRadius: '4px', display: 'block' }} />
                    </div>
                  </>
                </div>
              );
              imgIdx++;
              imgBloqueNum++;
            }
          }
          return bloques;
        })()}
        {/* SECCIÓN SOLO VIDEOS */}
        {(() => {
          // --- SECCIÓN SOLO VIDEOS (patrón filas garantizado y placeholders) ---
          const soloVideos = videoFiles.map((vid, idx) => ({
            tipo: 'video',
            src: process.env.PUBLIC_URL + '/media/' + vid,
            alt: `Video ${idx + 1}`
          }));
          const bloques = [];
          let vidIdx = 0;
          let vidBloqueNum = 1;

          // Si no hay videos, mensaje claro
          if (soloVideos.length === 0) {
            return <div style={{padding: '32px', color: '#b00', fontWeight: 'bold', textAlign: 'center', background: '#fff0f0', borderRadius: 12}}>No hay videos para mostrar en este álbum.</div>;
          }

          // 1. Primera fila: 4 bloques (rellena con placeholders)
          for (let j = 0; j < 4; j++) {
            if (vidIdx < soloVideos.length) {
              const r = soloVideos[vidIdx];
              bloques.push(
                <div key={`video-bloque-${vidIdx}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Video #${vidBloqueNum}`, meta: fechas[vidIdx % fechas.length], desc: 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', marginBottom: j === 3 ? '0px' : undefined, border: '2px solid #8e7cc3', borderRadius: 10, background: '#f6f8ff', minHeight: 180, display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative' }}>
                  <span style={{position:'absolute',top:8,left:8,background:'#8e7cc3',color:'#fff',padding:'2px 8px',borderRadius:8,fontSize:12}}>Video</span>
                  <BlogPost
                    video={r.src}
                    title={`Video #${vidBloqueNum}`}
                    meta={fechas[vidIdx % fechas.length]}
                    desc={'Un recuerdo en video para revivir juntos.'}
                  />
                </div>
              );
              vidIdx++;
              vidBloqueNum++;
            } else {
              bloques.push(
                <div key={`video-placeholder-1-${j}`} style={{border: '2px dashed #ccc', borderRadius: 10, background: '#f6f8ff', minHeight: 180, display: 'flex', alignItems: 'center', justifyContent: 'center', opacity: 0.3}}>
                  <span style={{color:'#aaa',fontSize:18}}>Vacío</span>
                </div>
              );
            }
          }

          // 2. Segunda fila: bloque grande (o placeholder)
          if (vidIdx < soloVideos.length) {
            const r = soloVideos[vidIdx];
            bloques.push(
              <div key={`video-large-${vidIdx}`} className="bloque-animado" onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Video #${vidBloqueNum}`, meta: fechas[vidIdx % fechas.length], desc: 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', gridColumn: '1 / -1', display: 'flex', alignItems: 'stretch', background: '#fff', borderRadius: '20px', minHeight: 220, padding: 0, width: '100%', maxHeight: 600, border: '2px solid #8e7cc3', marginTop: 12 }}>
                <>
                  <div style={{flex: '0 0 50%', minWidth: 0, height: '100%'}}>
                    <BlogPost
                      video={r.src}
                      title={`Video #${vidBloqueNum}`}
                      meta={fechas[vidIdx % fechas.length]}
                      desc={'Un recuerdo en video para revivir juntos.'}
                    />
                  </div>
                  <div className="bloque-animado-text" style={{ padding: '0 32px', flex: '1 1 50%', display: 'flex', flexDirection: 'column', justifyContent: 'center', background: 'rgba(255,255,255,0.82)', borderRadius: '0 0 20px 20px' }}>
                    <div style={{ fontWeight: 700, fontSize: 22, color: '#444', marginBottom: 8 }}>{`Video #${vidBloqueNum}`}</div>
                    <div style={{ color: '#7a7a7a', fontSize: 16, marginBottom: 8 }}>{fechas[vidIdx % fechas.length]}</div>
                    <div style={{ color: '#8e7cc3', fontSize: 17 }}>Un recuerdo en video para revivir juntos.</div>
                  </div>
                </>
              </div>
            );
            vidIdx++;
            vidBloqueNum++;
          } else {
            bloques.push(
              <div key={`video-placeholder-2`} style={{border: '2px dashed #ccc', borderRadius: 20, background: '#f6f8ff', minHeight: 220, display: 'flex', alignItems: 'center', justifyContent: 'center', opacity: 0.3, gridColumn: '1 / -1', marginTop: 12}}>
                <span style={{color:'#aaa',fontSize:22}}>Vacío</span>
              </div>
            );
          }

          // 3. Tercera fila: galería SOLO de videos (máx 10, rellena con placeholders)
          const galeria = [];
          let videosAgregados = 0;
          let galeriaIndex = vidIdx;
          for (let j = 0; j < 10; j++) {
            if (galeriaIndex < soloVideos.length) {
              const r = soloVideos[galeriaIndex];
              galeria.push(
                <div key={`video-mini-${galeriaIndex}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Video #${vidBloqueNum + videosAgregados}`, meta: fechas[galeriaIndex % fechas.length], desc: 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', display: 'inline-block', width: '132px', margin: '0 0.5px', border: '2px solid #8e7cc3', borderRadius: 8, background: '#f6f8ff', position:'relative' }}>
                  <span style={{position:'absolute',top:4,left:4,background:'#8e7cc3',color:'#fff',padding:'1px 6px',borderRadius:7,fontSize:11}}>Video</span>
                  <BlogPost
                    video={r.src}
                    title={`Video #${vidBloqueNum + videosAgregados}`}
                    meta={fechas[galeriaIndex % fechas.length]}
                    desc={'Un recuerdo en video para revivir juntos.'}
                  />
                </div>
              );
              videosAgregados++;
              galeriaIndex++;
            } else {
              galeria.push(
                <div key={`video-placeholder-gallery-${j}`} style={{border: '2px dashed #ccc', borderRadius: 8, background: '#f6f8ff', width: '132px', minHeight: 120, display: 'flex', alignItems: 'center', justifyContent: 'center', opacity: 0.3}}>
                  <span style={{color:'#aaa',fontSize:15}}>Vacío</span>
                </div>
              );
            }
          }
          if (galeria.length > 0) {
            bloques.push(
              <div key={`galeria-row-video-${vidIdx}`} style={{ gridColumn: '1 / -1', display: 'flex', justifyContent: 'center', gap: '8px', margin: '0', marginTop: 16 }}>
                {galeria}
              </div>
            );
          }
          vidIdx = galeriaIndex;
          vidBloqueNum += videosAgregados;

          // 4. Cuarta fila: bloque grande invertido (o placeholder)
          if (vidIdx < soloVideos.length) {
            const r = soloVideos[vidIdx];
            bloques.push(
