import React from 'react';
import './AlbumHome.css';
import BlogPost from './BlogPost';
import WideBlock from './WideBlock';
import GalleryRow from './GalleryRow';
import ModalMedia from './ModalMedia';

const imageFiles = [
  'Imagen1.jpg','Imagen2.jpg','Imagen3.jpg','Imagen4.jpg','Imagen5.jpg','Imagen6.jpg','Imagen7.jpg','Imagen8.jpg','Imagen9.jpg','Imagen10.jpg','Imagen11.jpg','Imagen12.jpg','Imagen13.jpg','Imagen14.jpg','Imagen15.jpg','Imagen16.jpg','Imagen17.jpg','Imagen18.jpg','Imagen19.jpg','Imagen20.jpg','Imagen21.jpg','Imagen22.jpg','Imagen23.jpg','Imagen24.jpg','Imagen25.jpg','Imagen26.jpg','Imagen27.jpg','Imagen28.jpg','Imagen29.jpg','Imagen30.jpg','Imagen31.jpg','Imagen32.jpg','Imagen33.jpg','Imagen34.jpg','Imagen35.jpg','Imagen36.jpg']

const videoFiles = [
  'video1.mp4','video2.mp4','video3.mp4','video4.mp4','video5.mp4','video6.mp4','video7.mp4','video8.mp4'
];


function AlbumHome() {
  // Fechas simuladas para el ejemplo de blog
  const fechas = [
    '20 de abril de 2025',
    '19 de abril de 2025',
    '18 de abril de 2025',
    '17 de abril de 2025',
    '16 de abril de 2025',
    '15 de abril de 2025',
    '14 de abril de 2025',
  ];

  const [modal, setModal] = React.useState({ open: false });

  const handleOpenModal = (data) => setModal({ open: true, ...data });
  const handleCloseModal = () => setModal({ open: false });

  return (
    <main className="album-home">
      <ModalMedia
        open={modal.open}
        onClose={handleCloseModal}
        tipo={modal.tipo}
        src={modal.src}
        alt={modal.alt}
        title={modal.title}
        meta={modal.meta}
        desc={modal.desc}
      />
      <header className="album-header">
        <h1>PortalAlbum</h1>
        <h2>2潞 Aniversario de Margarita & El铆</h2>
        <p className="album-desc">Un espacio para revivir juntos los mejores recuerdos en fotos, videos y canciones.</p>
      </header>
      <section className="blog-section">
        {/* Generar patr贸n repetitivo para todos los recuerdos */}
        {/* Todos los recuerdos como BlogPost iguales */}
        {(() => {
          const recuerdos = [
            ...imageFiles.map((img, idx) => ({
              tipo: 'imagen',
              src: process.env.PUBLIC_URL + '/media/' + img,
              alt: `Foto ${idx + 1}`
            })),
            ...videoFiles.map((vid, idx) => ({
              tipo: 'video',
              src: process.env.PUBLIC_URL + '/media/' + vid,
              alt: `Video ${idx + 1}`
            }))
          ];
          const bloques = [];
          let i = 0;
          let bloqueNum = 1;
          // Repetir patr贸n de 4 filas de forma c铆clica
          while (i < recuerdos.length) {
            // 1. Primera fila: 4 bloques
            for (let j = 0; j < 4 && i < recuerdos.length; j++, i++, bloqueNum++) {
              const r = recuerdos[i];
              bloques.push(
                <div key={`recuerdo${i}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`, meta: fechas[i % fechas.length], desc: r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', marginBottom: j === 3 ? '0px' : undefined }}>
                  {r.tipo === 'imagen' ? (
                    <img src={r.src} alt={`Recuerdo #${bloqueNum}`} className="post-image galeria-miniatura" style={{ width: '100%', borderRadius: '0', objectFit: 'cover' }} />
                  ) : (
                    <BlogPost
                      video={r.src}
                      title={`Video #${bloqueNum}`}
                      meta={fechas[i % fechas.length]}
                      desc={'Un recuerdo en video para revivir juntos.'}
                    />
                  )}
                </div>
              );
            }
            // 2. Segunda fila: solo un bloque grande (2 columnas)
            if (i < recuerdos.length) {
              const r = recuerdos[i];
              bloques.push(
                <div key={`recuerdo-large-${i}`} className="bloque-animado" onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`, meta: fechas[i % fechas.length], desc: r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', gridColumn: '1 / -1', display: 'flex', alignItems: 'stretch', background: '#fff', borderRadius: '20px', minHeight: 220, padding: 0, width: '100%', maxHeight: 600 }}>
                  {r.tipo === 'imagen' ? (
                    <>
                      <div style={{flex: '0 0 50%', minWidth: 0, height: '100%'}}>
                        <img src={r.src} alt={`Recuerdo #${bloqueNum}`} style={{ width: '100%', height: '100%', maxHeight: 600, objectFit: 'cover', borderRadius: '0 0 1.5px 1.5px', display: 'block' }} />
                      </div>
                      <div className="bloque-animado-text" style={{ padding: '0 32px', flex: '1 1 50%', display: 'flex', flexDirection: 'column', justifyContent: 'center', background: 'rgba(255,255,255,0.82)', borderRadius: '0 0 20px 20px' }}>
                        <div style={{ fontWeight: 700, fontSize: 22, color: '#444', marginBottom: 8 }}>{`Recuerdo #${bloqueNum}`}</div>
                        <div style={{ color: '#7a7a7a', fontSize: 16, marginBottom: 8 }}>{fechas[i % fechas.length]}</div>
                        <div style={{ color: '#8e7cc3', fontSize: 17 }}>Un recuerdo especial.</div>
                      </div>
                    </>
                  ) : (
                    <BlogPost
                      video={r.src}
                      title={`Video #${bloqueNum}`}
                      meta={fechas[i % fechas.length]}
                      desc={'Un recuerdo en video para revivir juntos.'}
                    />
                  )}
                </div>
              );
              i++;
              bloqueNum++;
            }
            // 3. Tercera fila: galer铆a horizontal de hasta 10 miniaturas SOLO de fotograf铆as
            const galeria = [];
            let fotosAgregadas = 0;
            let galeriaIndex = i;
            while (galeriaIndex < recuerdos.length && fotosAgregadas < 10) {
              const r = recuerdos[galeriaIndex];
              if (r.tipo === 'imagen') {
                galeria.push(
                  <div key={`recuerdo-mini-${galeriaIndex}`} onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: `Recuerdo #${bloqueNum + fotosAgregadas}`, meta: fechas[galeriaIndex % fechas.length], desc: 'Un recuerdo especial.' })} style={{ cursor: 'pointer', display: 'inline-block', width: '132px', margin: '0 0.5px' }}>
  <img src={r.src} alt={`Recuerdo #${bloqueNum + fotosAgregadas}`} className="post-image galeria-miniatura" style={{ width: '100%', borderRadius: '4px', objectFit: 'cover' }} />
</div>
                );
                fotosAgregadas++;
              }
              galeriaIndex++;
            }
            if (galeria.length > 0) {
              bloques.push(
                <div key={`galeria-row-${i}`} style={{ gridColumn: '1 / -1', display: 'flex', justifyContent: 'center', gap: '8px', margin: '0' }}>
                  {galeria}
                </div>
              );
            }
            i = galeriaIndex;
            bloqueNum += fotosAgregadas;
            // 4. Cuarta fila: igual a la segunda (bloque grande invertido)
            if (i < recuerdos.length) {
              const r = recuerdos[i];
              bloques.push(
                <div key={`recuerdo-large4-${i}`} className="bloque-animado" onClick={() => handleOpenModal({ tipo: r.tipo, src: r.src, alt: r.alt, title: r.tipo === 'imagen' ? `Recuerdo #${bloqueNum}` : `Video #${bloqueNum}`, meta: fechas[i % fechas.length], desc: r.tipo === 'imagen' ? 'Un recuerdo especial.' : 'Un recuerdo en video para revivir juntos.' })} style={{ cursor: 'pointer', gridColumn: '1 / -1', display: 'flex', alignItems: 'stretch', background: '#fff', borderRadius: '0 0 20px 20px', minHeight: 220, padding: 0, width: '100%', position: 'relative', top: '-2px', boxShadow: 'none' }}>
                  {r.tipo === 'imagen' ? (
                    <>
                      <div className="bloque-animado-text" style={{ padding: '0 32px', flex: '1 1 50%', display: 'flex', flexDirection: 'column', justifyContent: 'center', background: 'rgba(255,255,255,0.82)', borderRadius: '0 0 20px 20px' }}>
                        <div style={{ fontWeight: 700, fontSize: 22, color: '#444', marginBottom: 8 }}>{`Recuerdo #${bloqueNum}`}</div>
                        <div style={{ color: '#7a7a7a', fontSize: 16, marginBottom: 8 }}>{fechas[i % fechas.length]}</div>
                        <div style={{ color: '#8e7cc3', fontSize: 17 }}>Un recuerdo especial.</div>
                      </div>
                      <div style={{flex: '0 0 50%', minWidth: 0, height: '100%'}}>
                        <img src={r.src} alt={`Recuerdo #${bloqueNum}`} className="galeria-miniatura" style={{ width: '100%', height: '100%', maxHeight: 600, objectFit: 'cover', borderRadius: '4px', display: 'block' }} />
                      </div>
                    </>
                  ) : (
                    <BlogPost
                      video={r.src}
                      title={`Video #${bloqueNum}`}
                      meta={fechas[i % fechas.length]}
                      desc={'Un recuerdo en video para revivir juntos.'}
                    />
                  )}
                </div>
              );
              i++;
              bloqueNum++;
            }
          }
          // Canci贸n especial al final
          bloques.push(
            <div className="blog-post" style={{gridColumn: '1 / -1'}} key="cancion-especial">
              <div className="post-image empty-media" style={{display:'flex',alignItems:'center',justifyContent:'center',background:'#f7f7f7'}}>
                <span role="img" aria-label="Canci贸n" style={{fontSize:'2.4rem'}}></span>
              </div>
              <div className="post-body">
                <div className="post-title">Canci贸n especial</div>
                <div className="post-meta">20 de abril de 2025</div>
                <div className="post-desc">Escucha la canci贸n que acompa帽a este aniversario:</div>
                <div className="youtube-audio-wrapper" style={{marginTop:'8px'}}>
                  <iframe
                    width="100%"
                    height="60"
                    src="https://www.youtube.com/embed/BPidLpADlaM?autoplay=0&controls=1"
                    title="Canci贸n especial"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    style={{borderRadius: '10px', background: '#000'}}
                  ></iframe>
                </div>
              </div>
            </div>
          );
          return bloques;
        })()}

      </section>
    </main>
  );
