import React from 'react';
import './AlbumHome.css';

export default function ModalMedia({ open, onClose, tipo, src, alt, title, meta, desc, onPrev, onNext, hasPrev, hasNext }) {
  const [anim, setAnim] = React.useState('');
  const prevSrc = React.useRef(src);
  React.useEffect(() => {
    if (src !== prevSrc.current) {
      setAnim('modal-media-fade');
      prevSrc.current = src;
      const timeout = setTimeout(() => setAnim(''), 350);
      return () => clearTimeout(timeout);
    }
  }, [src, tipo]);
  if (!open) return null;
  return (
    <div className="modal-media-overlay" onClick={onClose}>
      <div className="modal-media-content" onClick={e => e.stopPropagation()}>
        <button className="modal-media-close" onClick={onClose}>&times;</button>
        <div className={`modal-media-media ${anim}`}>
          {tipo === 'imagen' ? (
            <img src={src} alt={alt} className="modal-media-img" />
          ) : (
            <video controls className="modal-media-video">
              <source src={src} type="video/mp4" />
              Tu navegador no soporta el video.
            </video>
          )}
        </div>
        <div className="modal-media-info">
          <div className="modal-media-title">{title}</div>
          <div className="modal-media-meta">{meta}</div>
          {/* Reproductor de YouTube según el índice del recuerdo */}
          {(() => {
            // Lista de links de YouTube en el orden proporcionado
            const youtubeLinks = [
              'https://www.youtube.com/embed/kjRZ-VkV9sE', // 1. Oh ms believer - twenty one pilots
              'https://www.youtube.com/embed/c5K3NQ08IQI', // 2. Luna - Zoé
              'https://www.youtube.com/embed/wLYk_qRjP6c', // 3. Labios rotos - Zoé
              'https://www.youtube.com/embed/MzNduKt0ylU', // 4. Brillas - leon Larregui
              'https://www.youtube.com/embed/MW5-mvkCl60', // 5. Malena - Ricardo Arjona
              'https://www.youtube.com/embed/Z2Vu0NZ6DnE', // 6. Ojos color sol - calle 13
              'https://www.youtube.com/embed/rwsRmnjEcYo', // 7. We don’t believe what’s on TV - twenty one pilots
              'https://www.youtube.com/embed/_OKxtMn4dDk', // 8. Arrullo de estrellas - Zoé
              'https://www.youtube.com/embed/waKVvHdkGI8', // 9. Pruébame a ti-Jósean Log
              'https://www.youtube.com/embed/wWMUKg6bmoA', // 10. Yo quiero ser-Malacates Trébol Shop
              'https://www.youtube.com/embed/o6KgErzzDXU', // 11. Quiero ver-café tacuba
              'https://www.youtube.com/embed/pf0KXLqu1BU', // 12. A la medida-Ricardo Arjona
              'https://www.youtube.com/embed/Sm5FW6MRyro', // 13. Adiós melancolía-Ricardo Arjona
              'https://www.youtube.com/embed/IPiA2ljRhvo', // 14. Compartir-Carla Morrison
              'https://www.youtube.com/embed/kJDbz7aICiA', // 15. Te quiero tanto-Kevin Kaarl
              'https://www.youtube.com/embed/difAEKi9wjw', // 16. Locos-León Larregui
              'https://www.youtube.com/embed/jrotr_I5uKU', // 17. Amor completo-Mon Laferte
              'https://www.youtube.com/embed/HboHK7cr55A', // 18. Te Quiero-Hombres G
              'https://www.youtube.com/embed/3CjIx9K3snI', // 19. Peace Of Mind-Imagine Dragons
              'https://www.youtube.com/embed/ctUDToc-qnE', // 20. Only-Imagine Dragons
              'https://www.youtube.com/embed/A8XP9TEf2y8', // 21. Smithereens-Twenty One Pilots
              'https://www.youtube.com/embed/oAJB67OSM7g', // 22. Valió La Pena-Jesse & Joy
              'https://www.youtube.com/embed/XnDhdSorVcs', // 23. Querer Mejor-Juanes
              'https://www.youtube.com/embed/95GAYvnHQW4', // 24. Luna-Juanes
              'https://www.youtube.com/embed/5-g2u3h2ocA', // 25. Para Tu Amor-Juanes
              'https://www.youtube.com/embed/fzNG5B_onMA', // 26. Tanto-Jesse & Joy
              'https://www.youtube.com/embed/or-7z1tBO6U', // 27. Es Por Ti-Juanes
              'https://www.youtube.com/embed/H-WutQ6vXDc', // 28. Te Amaré-Miguel Bosé
              'https://www.youtube.com/embed/hbhWDNRGciA', // 29. Tú de que vas-Franco De Vita
              'https://www.youtube.com/embed/sDMOP1cnVUo', // 30. Cielito de Abril-Mon Laferte
              'https://www.youtube.com/embed/zcwWvtjmViE', // 31. Día de Enero-Shakira
              'https://www.youtube.com/embed/HuQC33joOrc', // 32. Fin de Semana-Zoé
              'https://www.youtube.com/embed/zy9eSyyt0fs', // 33. Dame-Ricardo Arjona
              'https://www.youtube.com/embed/ot6eRIZaTps', // 34. Sonreir-Kurt
              'https://www.youtube.com/embed/w8SOcXfnQ_8', // 35. La Promesa-Melendi
              'https://www.youtube.com/embed/u8fHa5H5f7c', // 36. Vengo del Futuro-Kurt
              'https://www.youtube.com/embed/lYoWuaw5nSk', // 37. Riptide-Vance Joy
              'https://www.youtube.com/embed/MCH1tA-6JWs', // 38. Antología-Shakira
            ];
            // Determinar el índice del recuerdo (title debería tener el número)
            let idx = 0;
            if (title && title.match(/#(\d+)/)) {
              idx = parseInt(title.match(/#(\d+)/)[1], 10) - 1;
            }
            // Si hay un índice válido y un link correspondiente
            if (youtubeLinks[idx]) {
              return (
                <iframe
                  style={{borderRadius: '12px', margin: '10px auto', display: 'block'}}
                  width="100%"
                  height="80"
                  src={youtubeLinks[idx]}
                  title="YouTube video player"
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                  allowFullScreen
                  loading="lazy"
                ></iframe>
              );
            }
            return null;
          })()}
          <div className="modal-media-desc">{desc}</div>
        </div>
        <div className="modal-media-nav">
          <button className="modal-media-btn" onClick={onPrev} disabled={!hasPrev}>&larr; Anterior</button>
          <button className="modal-media-btn" onClick={onNext} disabled={!hasNext}>Siguiente &rarr;</button>
        </div>
      </div>
    </div>
  );
}
